import styled from "styled-components";

export const StyledForm = styled.form`
    max-width: 350px;
    width: 100%;
    margin: 2rem auto; 

    h2{
        margin-bottom: 1rem;
    }

    button, input{
        height: 35px;
        width: 100%;
        padding: 7px;
        outline: none;
        border-radius: 5px;
        border: 1px solid; ruby-align(220,220,220);
        margin-bottom: 1rem;
    }
`;
