import {createSlice, createAsyncThunk} from "@reduxjs/toolkit";
import axios from "axios"
import { url } from "./api";
import jwtDecode from "jwt-decode"

const initialState = {
    token: localStorage.getItem("token"),//check local storage for token, buitl in JS object
    name: "",
    email: "",
    _id: "",
    registerStatus: "",//pending, fullfilled, or reject
    registerError: "",
    loginStatus: "",
    loginError: "",
    userLoaded: false,
};

//action creator --> performs http request to backend, register user, get back token, then should be avaialble to action.payload, in case error you also add to pay;oad
export const registerUser = createAsyncThunk(
    "auth/registerUser",
    async (values, {rejectWithValue}) => {
        try{//handle error, http request a call to backend
            const token = await axios.post(`${url}/register`, {
                name: values.name,
                email: values.email,
                password: values.password
            });

            localStorage.setItem("token", token.data)

            return token.data
        }
        catch(err) {
            console.log(err.response.data);
            return rejectWithValue(err.response.data);
        }

    }
)

const authReducer = createSlice(
    {
        name: "auth",
        initialState,
        reducers: {}, //we will be creating reducer and action will automatically created by redux toolkit
        extraReducers: (builder) => {
            builder.addCase(registerUser.pending, (state, action) => {
                return { ...state, registerStatus: "pending"}
            });
            builder.addCase(registerUser.fulfilled, (state, action) => {
                if(action.payload){
                    const user = jwtDecode(action.payload)

                    return{
                        ...state,
                        token: action.payload,
                        name: user.name,
                        email: user.email,
                        _id: user._id,
                        registerStatus: "success"
                    }
                }
                else return state
            });
            builder.addCase(registerUser.reject, (state, action) => {
                return {
                    ...state,
                    registerStatus: "rejected",
                    registerError: action.payload
                }
            });
        },
    }//accepting object
);

export default authReducer.reducer
