import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from 'axios'

const initialState = {
    items: [], 
    status: null,
    error: null
}

export const productsFetch = createAsyncThunk( //action creator
    "products/productsFetch",
    async(id=null, { rejectWithValue }) => {
        try{


      const response = await axios.get("http://localhost:5000/productss") //async action
      return response?.data //? this will report an error incase we dont have a property data in reponse
        }
        catch(error){
            return rejectWithValue(error.response.data);
        }
    }
);

const productsSlice = createSlice({ //a slice is logic containing reducers and actions, wont have different files for them
    name: "products",
    initialState,
    reducers: {}, //generate action creators
    extraReducers: {
        [productsFetch.pending]: (state, action) => {
            state.status = "pending"
        },
        [productsFetch.fulfilled]: (state, action) => {
            state.status = "success"
            state.items = action.payload
        },
        [productsFetch.rejected]: (state, action) => {
            state.status = "rejected"
            state.error = action.payload
        },
    }, //only handle action types
});

export default productsSlice.reducer;
