import { productsApi, useGetAllProductsQuery } from "../features/productsApi";

const Home = () => {
    const { data, error, isLoading } = useGetAllProductsQuery();
    return (<div className="home-container">
        { isLoading ? (
        <p>Loading...</p>
        ) : error ? (
         <p>An error occurred</p>
        ):(
            <>
            <h2>New Arrivals</h2>
            <div className="products">
                {data?.map( product => <div key = {products.id} className="products"></div> )}
                    <h3>{productsApi.name}</h3>
                    <img src={product.image} alt={product.name}/>
                    <div className="details">
                        <span>{product.desc}</span>
                        <span className = "price">${product.price}</span>
                    </div>
                    <button>Add to Cart</button>
            </div>
            </>
         )} //the colon ':' is else in this case {error.data} in case you want to display the error
    </div>
    );
};

export default Home;
